/** @format */

import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import { useCallback } from 'react';
// import { tsParticles } from "tsparticles-engine"

const Particle = () => {
  const particlesInit = useCallback(async (engine) => {
    console.log(engine);
    // you can initiate the tsParticles instance (engine) here, adding custom shapes or presets
    // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
    // starting from v2 you can add only the features you need reducing the bundle size
    //  const load=new Loader(engine)
    await loadFull(engine);
  }, []);
  // const particlesLoaded = useCallback(async (container) => {
  //   console.log(container);
  // }, []);
  return (
    <Particles
      id='tsparticles-custom'
      init={particlesInit}
      // Loaded={particlesLoaded}
      options={{
        fullscreen: { enable: true, 'z-index': 0 },
        // manualParticles: 'IManualParticle[]',
        background: {
          color: '#f9a33f',
          opacity: 1,
        },
        fpsLimit: 25,
        interactivity: {
          detect_on: 'window',
          events: {
            onHover: {
              enable: true,
              mode: 'bubble',
              parallax: {
                enable: true,
                force: 30,
                smooth: 30,
              },
            },
            onClick: {
              enable: true,
              mode: 'push',
            },
            resize: true,
          },
          modes: {
            grab: {
              distance: 50,
              lineLinked: {
                opacity: 1,
              },
            },
            bubble: {
              distance: 200,
              size: 10,
              duration: 2,
              opacity: 0,
              speed: 2,
            },
            repulse: {
              distance: 100,
            },
            push: {
              particles_nb: 4,
            },
            remove: {
              particles_nb: 2,
            },
          },
        },
        particles: {
          color: {
            value: '#ffffff',
          },
          line_linked: {
            enable: true,
            distance: 2,
            color: '#d9dbd9',
            opacity: 1,
            width: 2,
          },
          move: {
            enable: true,
            speed: 2,
            direction: 'none',
            random: true,
            straight: false,
            out_mode: 'out',
            bounce: true,
            attract: {
              enable: false,
              rotateX: 1000,
              rotateY: 1000,
            },
          },
          number: {
            density: {
              enable: true,
              area: 1000,
            },
            limit: 0,
            value: 200,
          },
          opacity: {
            animation: {
              enable: true,
              minimumValue: 0.05,
              speed: 5,
              sync: true,
            },
            random: {
              enable: true,
              minimumValue: 0.05,
            },
            value: 1,
          },
          shape: {
            type: ['circle', 'star', 'polygon'],
          },
          size: {
            randmon: {
              enable: true,
              minimumValue: 1,
              value: 1,
            },
          },
        },
      }}
    />
  );
};

export default Particle;
