/** @format */

import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import { useCallback } from 'react';
// import { tsParticles } from "tsparticles-engine"

const Particle = () => {
  const particlesInit = useCallback(async (engine) => {
    console.log(engine);
    // you can initiate the tsParticles instance (engine) here, adding custom shapes or presets
    // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
    // starting from v2 you can add only the features you need reducing the bundle size
    //  const load=new Loader(engine)
    await loadFull(engine);
  }, []);
  // const particlesLoaded = useCallback(async (container) => {
  //   console.log(container);
  // }, []);
  //  let t = document.querySelector('tsparticles > .particles-js-canvas-el');
  return (
    <Particles
      id='tsparticles'
      init={particlesInit}
      // Loaded={particlesLoaded}
      options={{
        fullscreen: { enable: true, 'z-index': 0 },
        // manualParticles: 'IManualParticle[]',
        background: {
          color: '#f9a33f',
          opacity: 1,
        },
        // canvas: {
        //   el: t,
        //   w: t.offsetWidth,
        //   h: t.offsetHeight,
        // },
        fpsLimit: 25,
        interactivity: {
          detect_on: 'canvas',
          events: {
            onHover: {
              enable: true,
              mode: 'bubble',
              parallax: {
                enable: true,
                force: 30,
                smooth: 30,
              },
            },
            onClick: {
              enable: true,
              mode: 'push',
            },
            resize: true,
          },
          modes: {
            grab: {
              distance: 50,
              lineLinked: {
                opacity: 1,
              },
            },
            bubble: {
              distance: 200,
              size: 10,
              duration: 2,
              opacity: 0,
              speed: 2,
            },
            repulse: {
              distance: 100,
            },
            push: {
              particles_nb: 4,
            },
            remove: {
              particles_nb: 2,
            },
          },
        },

          particles: {
      number: { value: 400, density: { enable: !0, value_area: 800 } },
      color: { value: '#fff' },
      shape: {
        type: 'circle',
        stroke: { width: 0, color: '#ff0000' },
        polygon: { nb_sides: 5 },
        image: { src: '', width: 100, height: 100 },
      },
      opacity: {
        value: 1,
        random: !1,
        anim: { enable: !1, speed: 2, opacity_min: 0, sync: !1 },
      },
      size: {
        value: 20,
        random: !1,
        anim: { enable: !1, speed: 20, size_min: 0, sync: !1 },
      },
      line_linked: {
        enable: !0,
        distance: 100,
        color: '#fff',
        opacity: 1,
        width: 1,
      },
      move: {
        enable: 0,
        speed: 2,
        direction: 'none',
        random: !1,
        straight: !1,
        out_mode: 'out',
        bounce: !1,
        attract: { enable: !1, rotateX: 3e3, rotateY: 3e3 },
      },
      array: [],
    },
    interactivity: {
      detect_on: 'canvas',
      events: {
        onhover: { enable: !0, mode: 'grab' },
        onclick: { enable: !0, mode: 'push' },
        resize: !0,
      },
      modes: {
        grab: { distance: 100, line_linked: { opacity: 1 } },
        bubble: { distance: 200, size: 80, duration: 0.4 },
        repulse: { distance: 200, duration: 0.4 },
        push: { particles_nb: 4 },
        remove: { particles_nb: 2 },
      },
      mouse: {},
    },
    retina_detect: !1,
    fn: { interact: {}, modes: {}, vendors: {} },
    tmp: {},
      }}
    />
  );
};

export default Particle;
