/** @format */

import React, { useState, useEffect } from 'react';
import './Home.css';
import hero_img from '../../Assets/slider/hero-img.png';
import build_1 from '../../Assets/slider/Homepageimg1.jpg';
import build_2 from '../../Assets/slider/Homepageimg2.jpg';
import Particle from './Particle.jsx';
import { Slide } from '@material-ui/core';

const Home = () => {
  const [show, setShow] = useState(false);

  const [scrollPosition, setScrollPosition] = useState(0);
  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollPosition(position);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  console.log(scrollPosition);
  if (scrollPosition > 450) {
    setShow(true)
  }
    return (
      <div>
        {/* start banner area */}
        <section>
          <div className='saas-banner'>
            <Particle />
            <div className='container'>
              <div className='row'>
                <div className='col-lg-6 col-md-12'>
                  <div className='slide-image'>
                    <img alt='img' src={hero_img} />
                  </div>
                </div>
                <div className='col-lg-6 col-md-12 m-auto '>
                  <div className='hero-content'>
                    <h1>Unleashing Deeptech Innovation and Entrepreneurship</h1>
                    {/* <a href='#asfasf' target='_blank' className='banner-btn px-5'>
                    Apply for Nidhi Prayas
                  </a> */}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
        {/* end banner area */}
        {/* Start Overview Area */}
        <Slide direction='up' in={show} timeout={{ enter: 2500 }}>
          <section className='bg-light'>
            <div className='overview-item wow fadeInUp' data-wow-delay='.6s'>
              <div className='container con-width'>
                <div className='row align-items-center'>
                  <div className='col-lg-12 col-md-12'>
                    <div className='overview-content'>
                      <h2 className='section-title'>
                        What is <span className='span-color'>i</span>TIC ?
                      </h2>

                      <p>
                        iTIC is an incubator under the aegis of the Indian
                        Institute of Technology Hyderabad (IITH) and supported
                        by the Department of Science and Technology (DST),
                        Government of India. iTIC is a Not-For-Profit Society
                        that focuses on creating a supportive and nourishing
                        environment for budding entrepreneurs in the field of
                        technology. iTIC aims to help entrepreneurs leverage a
                        premier quality research and startup ecosystem to build
                        a comprehensive platform to practice innovation and
                        entrepreneurship so that these entrepreneurial ideas can
                        be turned into sustainable, scalable profit-making
                        business ventures.
                      </p>
                    </div>
                  </div>
                  <div className='row align-item-center ml-auto ptb-30'>
                    <div className='col-lg-6 col-md-12'>
                      <img src={build_1} alt='homepage' />
                    </div>
                    <div className='col-lg-6 col-md-12'>
                      <img src={build_2} alt='homepage' />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        </Slide>
        {/* End Overview Area */}
        {/* start impact area */}
      </div>
    );
};

export default Home;
